function text_counter(e1,e2,n){e2.html(n+' characters remaining').addClass('text-primary');e1.keyup(function(){var m=n-e1.val().length;e2.html(m+' characters remaining');if(m==0){e2.removeClass('text-primary').addClass('text-danger')}else if(e2.hasClass('text-danger')){e2.removeClass('text-danger').addClass('text-primary');}});}